import './App.css';
import { useState } from 'react';
import { Task } from './Task';

function App() {
  const [todoList, setTodolist] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [editTask, setEditTask] = useState(null);

  const handleChange = (event) => {
    setNewTask(event.target.value)
  }

  const addTask = () => {
    const task = {
      id: todoList.length === 0 ? 1 : todoList[todoList.length-1].id+1,
      taskName: newTask,
      completed: false,
    }
    setTodolist([...todoList, task])
    setNewTask("");
  }

  const deleteHandle = (id) => {
    const newTodoList = todoList.filter((task) => task.id !== id);
    setTodolist(newTodoList);
  }

  const completeTask = (id) => {
    setTodolist(
      todoList.map((task) => {
        if(task.id === id) {
          return {...task, completed: !task.completed}
        }
        else{
          return task;
        }
      })
    )
  }

  const editHandle = (id) => {
    const task = todoList.find((task) => task.id === id);
    setEditTask(task);
    setNewTask(task.taskName);
  }

  const updateTask = () => {
    setTodolist(
      todoList.map((task) => {
        if (task.id === editTask.id) {
          return {...task, taskName: newTask};
        } else {
          return task;
        }
      })
    );
    setEditTask(null);
    setNewTask("");
  }

  return (
    <div className="App">
      <div>
        <input value={newTask} onChange={handleChange}/>
        {editTask ? (
          <button onClick={updateTask}>Update Task</button>
        ) : (
          <button onClick={addTask}>Add Task</button>
        )}
      </div>
      <div>
        {todoList.map((task) => {
          return (
            <Task key={task.id} id={task.id} taskName={task.taskName} completed={task.completed}
              completeTask={completeTask} deleteHandle={deleteHandle} editHandle={editHandle}
              isEditing={editTask && editTask.id === task.id}/>
          );
        })}
      </div>
    </div>
  );
}

export default App;
