import React from 'react';

export const Task = (props) => {
return (
<div>
<div style={{ textDecoration: props.completed ? "line-through" : "" }}>
{props.taskName}
</div>
<button onClick={() => props.completeTask(props.id)}>Complete</button>
<button onClick={() => props.incompleteTask(props.id)}>Incomplete</button>
<button onClick={() => props.deleteHandle(props.id)}>Delete</button>
<button onClick={() => props.handleEditTask(props.taskName)}>Edit</button>
{props.handleSaveTask && (
<>
<input type="text" value={props.taskName} onChange={(e) => props.setNewTask(e.target.value)}/>
<button onClick={props.handleSaveTask}>Save</button>
<button onClick={props.handleCancelEdit}>Cancel</button>
</>
)}
</div>
);
};
