import './App.css';
import { useState } from 'react';
import { Task } from './Task';

function App() {
  const [todoList, setTodolist] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [editingTask, setEditingTask] = useState(null);

  const handleEditTask = (task) => {
    setEditingTask(task);
  };

  const handleCancelEdit = () => {
    setEditingTask(null);
  };

  const handleSaveTask = () => {
    const updatedTasks = newTask.map((task) =>
      task.id === editingTask.id ? { ...task, title: newTaskTitle } : task
    );
    setNewTask(updatedTasks);
    setEditingTask(null);
  };

  const handleChange = (event) => {
    setNewTask(event.target.value)
  }

  const addTask = () => {
    const task = {
      id: todoList.length === 0 ? 1 : todoList[todoList.length-1].id+1,
      taskName: newTask,
      completed: false,
    }
    setTodolist([...todoList, task])
  }

  const deleteHandle = (id) => {
    const newTodoList = todoList.filter((task) => task.id !== id);
    setTodolist(newTodoList);
  }

  const completeTask = (id) => {
    setTodolist(
      todoList.map((task) => {
        if(task.id === id) {
          return {...task, completed: !task.completed}
        }
        else{
          return task;
        }
      })
    )
  }

  return (
    <div className="App">
      <div>
        <input onChange={handleChange}/>
        <button onClick={addTask}>Add Task</button>
      </div>
      <div>
        {todoList.map((task) => {
          return (
            <Task id={task.id} taskName={task.taskName} completed={task.completed} 
            completeTask={completeTask} deleteHandle={deleteHandle}/>
          );
        })}
      </div>
    </div>
  );
}

export default App;
