import React, { useState } from 'react';

function Task({ index, title, completed, onEdit, onToggleCompleted, onDelete }) {
  const [editing, setEditing] = useState(false);
  const [newTitle, setNewTitle] = useState(title);

  const handleEdit = () => {
    if (editing) {
      onEdit(index, newTitle);
    }
    setEditing(!editing);
  };

  return (
    <div>
      <input
        type="checkbox"
        checked={completed}
        onChange={() => onToggleCompleted(index)}
      />
      {editing ? (
        <input
          type="text"
          value={newTitle}
          onChange={(e) => setNewTitle(e.target.value)}
        />
      ) : (
        <span>{title}</span>
      )}
      <button onClick={handleEdit}>{editing ? 'Save' : 'Edit'}</button>
      <button onClick={() => onDelete(index)}>Delete</button>
    </div>
  );
}

export default Task;
