import React, { useState } from 'react';

function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [input, setInput] = useState('');

  const addTask = () => {
    if (input) {
      setTasks([...tasks, { text: input, completed: false }]);
      setInput('');
    }
  };

  const deleteTask = (index) => {
    setTasks(tasks.filter((task, i) => i !== index));
  };

  const editTask = (index, newText) => {
    setTasks(tasks.map((task, i) => {
      if (i === index) {
        return { ...task, text: newText };
      } else {
        return task;
      }
    }));
  };

  const completeTask = (index) => {
    setTasks(tasks.map((task, i) => {
      if (i === index) {
        return { ...task, completed: true };
      } else {
        return task;
      }
    }));
  };

  const incompleteTask = (index) => {
    setTasks(tasks.map((task, i) => {
      if (i === index) {
        return { ...task, completed: false };
      } else {
        return task;
      }
    }));
  };

  return (
    <div>
      <h1>Todo List</h1>
      <ul>
        {tasks.map((task, i) => (
          <li key={i}>
            {task.completed ? <del>{task.text}</del> : task.text}
            <button onClick={() => deleteTask(i)}>Delete</button>
            <button onClick={() => editTask(i, prompt('Enter new task text:'))}>Edit</button>
            {task.completed ?
              <button onClick={() => incompleteTask(i)}>Incomplete</button> :
              <button onClick={() => completeTask(i)}>Complete</button>
            }
          </li>
        ))}
      </ul>
      <input type="text" value={input} onChange={(e) => setInput(e.target.value)} />
      <button onClick={addTask}>Add</button>
    </div>
  );
}

export default TodoList;
