import React, { useState } from 'react';

function TodoList() {
  const [tasks, setTasks] = useState([
    { id: 1, title: 'Task 1', completed: false },
    { id: 2, title: 'Task 2', completed: true },
    { id: 3, title: 'Task 3', completed: false },
  ]);

  const [showModal, setShowModal] = useState(false);
  const [taskToEdit, setTaskToEdit] = useState(null);
  const [newTaskTitle, setNewTaskTitle] = useState('');

  const handleAddTask = (title) => {
    const newTask = {
      id: tasks.length + 1,
      title: title,
      completed: false,
    };
    setTasks([...tasks, newTask]);
  };

  const handleEditTask = (task) => {
    setTaskToEdit(task);
    setNewTaskTitle(task.title);
    setShowModal(true);
  };

  const handleCancelEdit = () => {
    setTaskToEdit(null);
    setNewTaskTitle('');
    setShowModal(false);
  };

  const handleSaveTask = () => {
    const updatedTasks = tasks.map((task) =>
      task.id === taskToEdit.id ? { ...task, title: newTaskTitle } : task
    );
    setTasks(updatedTasks);
    setTaskToEdit(null);
    setNewTaskTitle('');
    setShowModal(false);
  };

  const handleDeleteTask = (task) => {
    const updatedTasks = tasks.filter((t) => t.id !== task.id);
    setTasks(updatedTasks);
  };

  const handleCompleteTask = (task) => {
    const updatedTasks = tasks.map((t) =>
      t.id === task.id ? { ...t, completed: true } : t
    );
    setTasks(updatedTasks);
  };

  const handleIncompleteTask = (task) => {
    const updatedTasks = tasks.map((t) =>
      t.id === task.id ? { ...t, completed: false } : t
    );
    setTasks(updatedTasks);
  };

  return (
    <div>
      <h1>Todo List</h1>
      <button onClick={() => setShowModal(true)}>Add Task</button>
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            <span style={{ textDecoration: task.completed ? 'line-through' : '' }}>
              {task.title}
            </span>
            <button onClick={() => handleEditTask(task)}>Edit</button>
            <button onClick={() => handleDeleteTask(task)}>Delete</button>
            {task.completed ? (
              <button onClick={() => handleIncompleteTask(task)}>Incomplete</button>
            ) : (
              <button onClick={() => handleCompleteTask(task)}>Complete</button>
            )}
          </li>
        ))}
      </ul>
      {showModal && (
        <div>
          <div>
            <label htmlFor="new-task">New Task:</label>
            <input
              id="new-task"
              type="text"
              value={newTaskTitle}
              onChange={(e) => setNewTaskTitle(e.target.value)}
            />
          </div>
          <div>
            <button onClick={handleSaveTask}>Save</button>
            <button onClick={handleCancelEdit}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default TodoList;
