import React, { useState } from 'react';

const TodoList = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [editTask, setEditTask] = useState('');
  const [editing, setEditing] = useState(false);

  const addTask = () => {
    setTasks([...tasks, { name: newTask, completed: false }]);
    setNewTask('');
  };

  const deleteTask = (taskToDelete) => {
    setTasks(tasks.filter((task) => task !== taskToDelete));
  };

  const completeTask = (completedTask) => {
    setTasks(
      tasks.map((task) =>
        task === completedTask ? { ...task, completed: !task.completed } : task
      )
    );
  };

  const editTaskHandler = (taskToEdit) => {
    setEditing(true);
    setEditTask(taskToEdit);
    setNewTask(taskToEdit.name);
  };

  const saveTask = () => {
    setTasks(
      tasks.map((task) =>
        task === editTask ? { ...task, name: newTask } : task
      )
    );
    setEditing(false);
    setEditTask('');
    setNewTask('');
  };

  const cancelEdit = () => {
    setEditing(false);
    setEditTask('');
    setNewTask('');
  };

  return (
    <div>
      <h1>Todo List</h1>
      {tasks.map((task) => (
        <div
          key={task.name}
          className={`task ${task.completed ? 'completed' : ''}`}
        >
          <input
            type='checkbox'
            checked={task.completed}
            onChange={() => completeTask(task)}
          />
          {editing && editTask === task ? (
            <>
              <input
                type='text'
                value={newTask}
                onChange={(e) => setNewTask(e.target.value)}
              />
              <button onClick={saveTask}>Save</button>
              <button onClick={cancelEdit}>Cancel</button>
            </>
          ) : (
            <>
              <span>{task.name}</span>
              <button onClick={() => editTaskHandler(task)}>Edit</button>
              <button onClick={() => deleteTask(task)}>Delete</button>
            </>
          )}
        </div>
      ))}
      <div>
        <input
          type='text'
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
        <button onClick={addTask}>Add Task</button>
      </div>
    </div>
  );
};

export default TodoList;
