export const Task = (props) => {
    return(
        <div>
            {props.editingTask?.id === props.id ? (
              <div>
                <input
                  type="text"
                  value={props.taskName}
                  onChange={(e) => props.setNewTask(e.target.value)}
                />
                <button onClick={props.handleSaveTask}>Save</button>
                <button onClick={props.handleCancelEdit}>Cancel</button>
              </div>
            ) : (<div>
                    <span style={{ textDecoration: props.completed ? 'line-through' : '' }}>{props.id} {props.taskName}</span>
                    <button onClick={() => props.handleEditTask(props.id)}>Edit</button>
                    <button onClick={() => props.deleleHandle(props.id)}>Delete</button>
                    {props.completed ? (
                    <button onClick={() => props.handleIncompleteTask(props.id)}>
                        Incomplete
                    </button>
                    ) : (
                    <button onClick={() => props.handleCompleteTask(props.id)}>
                        Complete
                    </button>
                    )}
                </div>)}
        </div>






        // <div style={{backgroundColor: props.completed ? "green" : "white"}}>
        //     <h1>{props.id} {props.taskName}</h1>
        //     <button onClick={() => {props.completeTask(props.id)}}>Complete</button>
        //     <button onClick={() => {props.deleteHandle(props.id)}}>X</button>
        // </div>
    );
}
