import React, { useState } from 'react';
import './App';

const TodoList = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [editTask, setEditTask] = useState('');
  const [editing, setEditing] = useState(false);

  const addTask = () => {
    setTasks([...tasks, { name: newTask, completed: false }]);
    setNewTask('');
  };

  const deleteTask = (taskToDelete) => {
    setTasks(tasks.filter((task) => task !== taskToDelete));
  };

  const completeTask = (completedTask) => {
    setTasks(
      tasks.map((task) => {
        if(task.name === completedTask) {
          return {...task, completed: true}
        }
        else{
          return task;
        }
      })
    )
  }


  const incompleteTask = (completedTask) => {
    setTasks(
      tasks.map((task) => {
        if(task.name === completedTask) {
          return {...task, completed: false}
        }
        else{
          return task;
        }
      })
    )
  }


  const editTaskHandler = (taskToEdit) => {
    setEditing(true);
    setEditTask(taskToEdit);
    setNewTask(taskToEdit.name);
  };

  const saveTask = () => {
    setTasks(
      tasks.map((task) =>
        task === editTask ? { ...task, name: newTask } : task
      )
    );
    setEditing(false);
    setEditTask('');
    setNewTask('');
  };

  const cancelEdit = () => {
    setEditing(false);
    setEditTask('');
    setNewTask('');
  };

  return (
    <div>
      <h1>Todo List</h1>
      <div>
        <input
          type='text'
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
        <button onClick={addTask}>Add Task</button>
      </div>
      {tasks.map((task) => (
        <div key={task.name}>
          {editing && editTask === task ? (
            <>
              <input
                type='text'
                value={newTask}
                onChange={(e) => setNewTask(e.target.value)}
              />
              <button onClick={saveTask}>Save</button>
              <button onClick={cancelEdit}>Cancel</button>
            </>
          ) : (
            <>
              <span style={{textDecoration: task.completed ? 'line-through' : '' }}>{task.name}</span>
              {task.completed ? (
                <button onClick={() => incompleteTask(task.name)}>
                    Incomplete
                </button>
                ) : (
                <button onClick={() => completeTask(task.name)}>
                    Complete
                </button>
              )}
              <button onClick={() => editTaskHandler(task)}>Edit</button>
              <button onClick={() => deleteTask(task)}>Delete</button>
            </>
          )}
        </div>
      ))}
    </div>
  );
};

export default TodoList;
